import React from "react";
import ReactDOM from "react-dom";

const Header = () => <h1>{"Pomodoro Clock"}</h1>;

const SetTimer = ({type, label, value, handleClick}) => (
    <div className={"SetTimer"}>
        <div id={`${type}-label`}>{label}</div>
        <div className={"SetTimer-controls"}>
            <button
                type={"button"}
                id={`${type}-decrement`}
                onClick={() => handleClick(false, `${type}Value`)}>
                &#10134;
            </button>
            <h1 id={`${type}-length`}>{value}</h1>
            <button
                type={"button"}
                id={`${type}-increment`}
                onClick={() => handleClick(true, `${type}Value`)}>
                &#10133;
            </button>
        </div>
    </div>
);

const Timer = ({mode, time}) => (
    <div className={"Timer"}>
        <h1 id={"timer-label"}>{mode === "session" ? "Session " : "Break "}</h1>
        <h1 id={"time-left"}>{time}</h1>
    </div>
);

const Controls = ({active, handleReset, handlePlayPause}) => (
    <div className={"Controls"}>
        <button type={"button"} id={"start_stop"} onClick={handlePlayPause}>
            {active ? <span>&#9208;</span> : <span>&#9199;</span>}
        </button>
        <button id={"reset"} onClick={handleReset}>
            &#128260;
        </button>
    </div>
);

const formatedtime = time => {
    const secRestante = time / 1000;
    const min = Math.floor(secRestante / 60);
    const sec = secRestante % 60;

    return `${min.toString().padStart(2, "0")} : ${sec
        .toString()
        .padStart(2, "0")}`;
};

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            breakValue: 5,
            sessionValue: 25,
            time: 25 * 60 * 1000,
            active: false,
            mode: "session",
        };
        this.handleSetTimers = this.handleSetTimers.bind(this);
        this.handlePlayPause = this.handlePlayPause.bind(this);
        this.handleReset = this.handleReset.bind(this);
    }

    componentDidUpdate(prevProps) {
        if (this.state.time === 0 && this.state.mode === "session") {
            this.setState({
                time: this.state.breakValue * 60 * 1000,
                mode: "break",
            });
            this.audio.play();
        }
        if (this.state.time === 0 && this.state.mode === "break") {
            this.setState({
                time: this.state.sessionValue * 60 * 1000,
                mode: "session",
            });
            this.audio.play();
        }
    }

    handleSetTimers(inc, type) {
        if (inc && this.state[type] === 60) {
            return;
        }
        if (!inc && this.state[type] === 1) {
            return;
        }
        this.setState({[type]: this.state[type] + (inc ? 1 : -1)});
    }

    handlePlayPause() {
        if (this.state.active) {
            this.setState({active: false}, () => clearInterval(this.pomodoro));
        } else {
            if (!this.state.touched) {
                this.setState(
                    {
                        time: this.state.sessionValue * 60 * 1000,
                        active: true,
                        touched: true,
                    },
                    () =>
                        (this.pomodoro = setInterval(
                            () => this.setState({time: this.state.time - 1000}),
                            1000,
                        )),
                );
            } else {
                this.setState(
                    {
                        active: true,
                        touched: true,
                    },
                    () =>
                        (this.pomodoro = setInterval(
                            () => this.setState({time: this.state.time - 1000}),
                            1000,
                        )),
                );
            }
        }
    }

    handleReset() {
        this.setState({
            breakValue: 5,
            sessionValue: 25,
            time: 25 * 60 * 1000,
            active: false,
            mode: "session",
            touched: false,
        });
        this.audio.pause();
        this.audio.currentTime = 0;
        clearInterval(this.pomodoro);
    }

    render() {
        return (
            <div>
                <Header />
                <div className={"settings"}>
                    <SetTimer
                        type={"break"}
                        label={"Break Length"}
                        value={this.state.breakValue}
                        handleClick={this.handleSetTimers}
                    />
                    <SetTimer
                        type={"session"}
                        label={"Session Length"}
                        value={this.state.sessionValue}
                        handleClick={this.handleSetTimers}
                    />
                </div>
                <Timer
                    mode={this.state.mode}
                    time={formatedtime(this.state.time)}
                />
                <Controls
                    active={this.state.active}
                    handleReset={this.handleReset}
                    handlePlayPause={this.handlePlayPause}
                />
                <audio
                    id={"beep"}
                    src={
                        "https://s3-us-west-1.amazonaws.com/benjaminadk/Data+synth+beep+high+and+sweet.mp3"
                    }
                    ref={ref => (this.audio = ref)}
                />
            </div>
        );
    }
}

ReactDOM.render(<App />, document.querySelector("#root"));
